/** * MindatScrape * * @author David Krystall *         $ Jan 11 2021 10:46 PM */case class MindatAnchor(relativeLink: String) {  private var linkWithoutQuotes: String = relativeLink.splitAt(1)._2;  if (linkWithoutQuotes.contains("\"")) {    linkWithoutQuotes = linkWithoutQuotes.substring(0, linkWithoutQuotes.length - 1);  };  val link: String = "https://www.mindat.org/" + linkWithoutQuotes;}